import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Sidebar from './components/Sidebar';
import TimelineChart from './components/TimelineChart';
import HistoryChart from './components/HistoryChart';
import './App.css';

const API_BASE_URL = 'http://localhost:8000/api';

function App() {
  const [selectedZona, setSelectedZona] = useState(null);
  const [zonas, setZonas] = useState([]);
  const [eventosCarros, setEventosCarros] = useState([]);
  const [eventosAnimales, setEventosAnimales] = useState([]);
  const [alertas, setAlertas] = useState([]);
  const [loading, setLoading] = useState(false);
  const [notificaciones, setNotificaciones] = useState({});
  const [lastEventTimestamp, setLastEventTimestamp] = useState({});

  // Cargar zonas
  useEffect(() => {
    const fetchZonas = async () => {
      try {
        const response = await axios.get(`${API_BASE_URL}/zonas`);
        setZonas(response.data);
        if (response.data.length > 0 && !selectedZona) {
          setSelectedZona(response.data[0].id);
        }
      } catch (error) {
        console.error('Error cargando zonas:', error);
      }
    };
    fetchZonas();
  }, []);

  // Polling para eventos
  useEffect(() => {
    if (!selectedZona) return;

    const fetchEventos = async () => {
      try {
        setLoading(true);
        const [carrosRes, animalesRes, alertasRes] = await Promise.all([
          axios.get(`${API_BASE_URL}/eventos-carros`, { params: { zona_id: selectedZona } }),
          axios.get(`${API_BASE_URL}/eventos-animales`, { params: { zona_id: selectedZona } }),
          axios.get(`${API_BASE_URL}/alertas`, { params: { zona_id: selectedZona } }),
        ]);

        // Comparar timestamps para detectar cambios
        const carrosTimestamp = carrosRes.data[0]?.hora_inicio || 0;
        const animalesTimestamp = animalesRes.data[0]?.hora || 0;
        const alertasTimestamp = alertasRes.data[0]?.hora || 0;

        const lastTimestamp = lastEventTimestamp[selectedZona] || 0;
        const hasChanges = carrosTimestamp > lastTimestamp || animalesTimestamp > lastTimestamp || alertasTimestamp > lastTimestamp;

        if (hasChanges || !lastEventTimestamp[selectedZona]) {
          setEventosCarros(carrosRes.data);
          setEventosAnimales(animalesRes.data);
          setAlertas(alertasRes.data);
          setLastEventTimestamp(prev => ({
            ...prev,
            [selectedZona]: Math.max(carrosTimestamp, animalesTimestamp, alertasTimestamp)
          }));
        }

        // Verificar cambios en otras zonas
        for (const zona of zonas) {
          if (zona.id !== selectedZona) {
            try {
              const res = await axios.get(`${API_BASE_URL}/eventos-carros`, { 
                params: { zona_id: zona.id, limit: 1 } 
              });
              const zonaTimestamp = res.data[0]?.hora_inicio || 0;
              const lastZonaTimestamp = lastEventTimestamp[zona.id] || 0;

              if (zonaTimestamp > lastZonaTimestamp) {
                setNotificaciones(prev => ({
                  ...prev,
                  [zona.id]: true
                }));
                setLastEventTimestamp(prev => ({
                  ...prev,
                  [zona.id]: zonaTimestamp
                }));
              }
            } catch (error) {
              console.error(`Error checking zona ${zona.id}:`, error);
            }
          }
        }

        setLoading(false);
      } catch (error) {
        console.error('Error cargando eventos:', error);
        setLoading(false);
      }
    };

    const interval = setInterval(fetchEventos, 2000);
    fetchEventos(); // Llamada inmediata

    return () => clearInterval(interval);
  }, [selectedZona, zonas, lastEventTimestamp]);

  return (
    <div className="app-container">
      <Sidebar
        zonas={zonas}
        selectedZona={selectedZona}
        onSelectZona={(zonaId) => {
          setNotificaciones(prev => ({ ...prev, [zonaId]: false }));
          setSelectedZona(zonaId);
        }}
        notificaciones={notificaciones}
      />
      <div className="main-content">
        <header className="app-header">
          <h1>ðŸš— IoT Monitoring Dashboard</h1>
          {selectedZona && (
            <p className="zona-info">
              Zona Activa: <strong>{zonas.find(z => z.id === selectedZona)?.nombre}</strong>
            </p>
          )}
        </header>

        {loading && <div className="loading">Cargando...</div>}

        {selectedZona && (
          <div className="charts-container">
            <TimelineChart
              eventosCarros={eventosCarros}
              eventosAnimales={eventosAnimales}
              alertas={alertas}
            />
            <HistoryChart
              eventosCarros={eventosCarros}
              eventosAnimales={eventosAnimales}
            />
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
