import React, { useState, useMemo } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { ZoomIn, ZoomOut } from 'lucide-react';
import '../styles/TimelineChart.css';

function TimelineChart({ eventosCarros, eventosAnimales, alertas }) {
  const [zoomLevel, setZoomLevel] = useState(1);
  const [tipoSeleccionado, setTipoSeleccionado] = useState('carros'); // 'carros', 'animales', 'alertas'

  // Procesar datos para cada tipo de evento
  const dataPorTipo = useMemo(() => {
    // Carros
    const carrosData = eventosCarros.map(evento => {
      const date = new Date(evento.hora_inicio);
      const dateStr = date.toLocaleDateString('es-ES');
      const timeStr = date.toLocaleTimeString('es-ES', { hour: '2-digit', minute: '2-digit' });
      const label = `${dateStr} ${timeStr}`;
      return { label, timestamp: date.getTime(), value: 1 };
    }).sort((a, b) => a.timestamp - b.timestamp);

    // Animales
    const animalesData = eventosAnimales.map(evento => {
      const date = new Date(evento.hora);
      const dateStr = date.toLocaleDateString('es-ES');
      const timeStr = date.toLocaleTimeString('es-ES', { hour: '2-digit', minute: '2-digit' });
      const label = `${dateStr} ${timeStr}`;
      return { label, timestamp: date.getTime(), value: 1 };
    }).sort((a, b) => a.timestamp - b.timestamp);

    // Alertas
    const alertasData = alertas.map(alerta => {
      const date = new Date(alerta.hora);
      const dateStr = date.toLocaleDateString('es-ES');
      const timeStr = date.toLocaleTimeString('es-ES', { hour: '2-digit', minute: '2-digit' });
      const label = `${dateStr} ${timeStr}`;
      return { label, timestamp: date.getTime(), value: 1 };
    }).sort((a, b) => a.timestamp - b.timestamp);

    return {
      carros: carrosData,
      animales: animalesData,
      alertas: alertasData
    };
  }, [eventosCarros, eventosAnimales, alertas]);

  const visibleData = useMemo(() => 
    dataPorTipo[tipoSeleccionado].slice(-Math.ceil(20 * zoomLevel)),
    [dataPorTipo, tipoSeleccionado, zoomLevel]
  );

  const handleZoomIn = () => setZoomLevel(prev => Math.min(prev + 0.5, 3));
  const handleZoomOut = () => setZoomLevel(prev => Math.max(prev - 0.5, 1));

  return (
    <div className="timeline-chart">
      <div className="chart-header">
        <h2>üìä L√≠nea de Tiempo - Eventos por Hora</h2>
        <div className="zoom-controls">
          <button onClick={handleZoomOut} title="Alejar">
            <ZoomOut size={20} /> Alejar
          </button>
          <span className="zoom-level">{Math.round(zoomLevel * 100)}%</span>
          <button onClick={handleZoomIn} title="Acercar">
            <ZoomIn size={20} /> Acercar
          </button>
        </div>
      </div>

      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={visibleData} margin={{ top: 5, right: 30, left: 0, bottom: 80 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis 
            dataKey="label" 
            angle={-45}
            textAnchor="end"
            height={100}
          />
          <YAxis />
          <Tooltip 
            contentStyle={{
              backgroundColor: '#1e1e1e',
              border: '1px solid #444',
              borderRadius: '4px',
              color: '#fff'
            }}
            labelFormatter={(label) => label}
          />
          <Legend />
          <Line type="monotone" dataKey="carros" stroke="#ff6b6b" strokeWidth={2} dot={{ r: 4 }} name="Carros" isAnimationActive={false} />
          <Line type="monotone" dataKey="animales" stroke="#4ecdc4" strokeWidth={2} dot={{ r: 4 }} name="Animales" isAnimationActive={false} />
          <Line type="monotone" dataKey="alertas" stroke="#ffd93d" strokeWidth={2} dot={{ r: 4 }} name="Alertas" isAnimationActive={false} />
        </LineChart>
      </ResponsiveContainer>

      <div className="chart-stats">
        <div className="stat">
          <span className="stat-label">üöó Total Carros:</span>
          <span className="stat-value">{eventosCarros.length}</span>
        </div>
        <div className="stat">
          <span className="stat-label">ü¶Å Total Animales:</span>
          <span className="stat-value">{eventosAnimales.length}</span>
        </div>
        <div className="stat">
          <span className="stat-label">‚ö†Ô∏è Total Alertas:</span>
          <span className="stat-value">{alertas.length}</span>
        </div>
      </div>
    </div>
  );
}

export default React.memo(TimelineChart);
