import React, { useMemo } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import '../styles/HistoryChart.css';

function HistoryChart({ eventosCarros, eventosAnimales }) {
  // Procesar datos por hora y dÃ­a (cachear)
  const { hourly, daily } = useMemo(() => {
    const hourly = {};
    const daily = {};

    const processEvent = (evento, isAnimal = false) => {
      const date = new Date(isAnimal ? evento.hora : evento.hora_inicio);

      // Por hora
      const hourKey = date.toLocaleTimeString('es-ES', { hour: '2-digit', minute: '2-digit' });
      hourly[hourKey] = (hourly[hourKey] || 0) + 1;

      // Por dÃ­a
      const dayKey = date.toLocaleDateString('es-ES');
      daily[dayKey] = (daily[dayKey] || 0) + 1;
    };

    eventosCarros.forEach(e => processEvent(e, false));
    eventosAnimales.forEach(e => processEvent(e, true));

    return {
      hourly: Object.entries(hourly).map(([time, count]) => ({ time, eventos: count })),
      daily: Object.entries(daily).map(([date, count]) => ({ date, eventos: count }))
    };
  }, [eventosCarros, eventosAnimales]);

  return (
    <div className="history-chart-container">
      <div className="history-chart">
        <h3>ðŸ“ˆ Historial por Hora</h3>
        <ResponsiveContainer width="100%" height={250}>
          <BarChart data={hourly} margin={{ top: 5, right: 30, left: 0, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="time" />
            <YAxis />
            <Tooltip 
              contentStyle={{
                backgroundColor: '#1e1e1e',
                border: '1px solid #444',
                borderRadius: '4px',
                color: '#fff'
              }}
            />
            <Bar dataKey="eventos" fill="#8884d8" name="Eventos" />
          </BarChart>
        </ResponsiveContainer>
      </div>

      <div className="history-chart">
        <h3>ðŸ“… Historial por DÃ­a</h3>
        <ResponsiveContainer width="100%" height={250}>
          <BarChart data={daily} margin={{ top: 5, right: 30, left: 0, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis 
              dataKey="date" 
              angle={-45}
              textAnchor="end"
              height={80}
            />
            <YAxis />
            <Tooltip 
              contentStyle={{
                backgroundColor: '#1e1e1e',
                border: '1px solid #444',
                borderRadius: '4px',
                color: '#fff'
              }}
            />
            <Bar dataKey="eventos" fill="#82ca9d" name="Eventos" />
          </BarChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
}

export default HistoryChart;
