import React, { useState } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { ZoomIn, ZoomOut } from 'lucide-react';
import '../styles/TimelineChart.css';

function TimelineChart({ eventosCarros, eventosAnimales, alertas }) {
  const [zoomLevel, setZoomLevel] = useState(1);

  // Procesar datos para la l√≠nea de tiempo
  const processTimelineData = () => {
    const data = {};

    // Agrupar por hora
    eventosCarros.forEach(evento => {
      const hour = new Date(evento.hora_inicio).toLocaleTimeString('es-ES', { 
        hour: '2-digit', 
        minute: '2-digit' 
      });
      data[hour] = (data[hour] || 0) + 1;
    });

    eventosAnimales.forEach(evento => {
      const hour = new Date(evento.hora).toLocaleTimeString('es-ES', { 
        hour: '2-digit', 
        minute: '2-digit' 
      });
      data[hour] = (data[hour] || 0) + 1;
    });

    return Object.entries(data).map(([time, count]) => ({
      time,
      eventos: count,
      carros: eventosCarros.filter(e => 
        new Date(e.hora_inicio).toLocaleTimeString('es-ES', { hour: '2-digit', minute: '2-digit' }) === time
      ).length,
      animales: eventosAnimales.filter(e => 
        new Date(e.hora).toLocaleTimeString('es-ES', { hour: '2-digit', minute: '2-digit' }) === time
      ).length,
      alertas: alertas.filter(a => 
        new Date(a.hora).toLocaleTimeString('es-ES', { hour: '2-digit', minute: '2-digit' }) === time
      ).length,
    }));
  };

  const timelineData = processTimelineData();
  const visibleData = timelineData.slice(-Math.ceil(20 * zoomLevel));

  const handleZoomIn = () => setZoomLevel(prev => Math.min(prev + 0.5, 3));
  const handleZoomOut = () => setZoomLevel(prev => Math.max(prev - 0.5, 1));

  return (
    <div className="timeline-chart">
      <div className="chart-header">
        <h2>üìä L√≠nea de Tiempo - Eventos por Hora</h2>
        <div className="zoom-controls">
          <button onClick={handleZoomOut} title="Alejar">
            <ZoomOut size={20} /> Alejar
          </button>
          <span className="zoom-level">{Math.round(zoomLevel * 100)}%</span>
          <button onClick={handleZoomIn} title="Acercar">
            <ZoomIn size={20} /> Acercar
          </button>
        </div>
      </div>

      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={visibleData} margin={{ top: 5, right: 30, left: 0, bottom: 5 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis 
            dataKey="time" 
            angle={-45}
            textAnchor="end"
            height={80}
          />
          <YAxis />
          <Tooltip 
            contentStyle={{
              backgroundColor: '#1e1e1e',
              border: '1px solid #444',
              borderRadius: '4px',
              color: '#fff'
            }}
          />
          <Legend />
          <Line type="monotone" dataKey="carros" stroke="#ff6b6b" strokeWidth={2} dot={{ r: 4 }} name="Carros" />
          <Line type="monotone" dataKey="animales" stroke="#4ecdc4" strokeWidth={2} dot={{ r: 4 }} name="Animales" />
          <Line type="monotone" dataKey="alertas" stroke="#ffd93d" strokeWidth={2} dot={{ r: 4 }} name="Alertas" />
        </LineChart>
      </ResponsiveContainer>

      <div className="chart-stats">
        <div className="stat">
          <span className="stat-label">üöó Total Carros:</span>
          <span className="stat-value">{eventosCarros.length}</span>
        </div>
        <div className="stat">
          <span className="stat-label">ü¶Å Total Animales:</span>
          <span className="stat-value">{eventosAnimales.length}</span>
        </div>
        <div className="stat">
          <span className="stat-label">‚ö†Ô∏è Total Alertas:</span>
          <span className="stat-value">{alertas.length}</span>
        </div>
      </div>
    </div>
  );
}

export default TimelineChart;
