import React, { useState, useMemo } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import '../styles/HistoryChart.css';

function HistoryChart({ eventosCarros, eventosAnimales, alertas }) {
  const [tipoSeleccionado, setTipoSeleccionado] = useState('carros');
  const [diaSeleccionado, setDiaSeleccionado] = useState(null);

  // Procesar datos por hora y d√≠a (cachear)
  const { daily, horasPorDia, allHours } = useMemo(() => {
    const daily = {};
    const horasPorDia = {}; // { 'dd/MM/yyyy': { '00:00': count, '01:00': count, ... } }
    const allHours = []; // Para gr√°fica principal con d√≠a, fecha y hora

    const processEvent = (evento, isAnimal = false) => {
      let date;
      if (isAnimal === 'alerta') {
        date = new Date(evento.hora);
      } else if (isAnimal) {
        date = new Date(evento.hora);
      } else {
        date = new Date(evento.hora_inicio);
      }

      // Por d√≠a - agrupar por fecha completa
      const dayKey = date.toLocaleDateString('es-ES');
      daily[dayKey] = (daily[dayKey] || 0) + 1;

      // Por hora dentro de cada d√≠a
      if (!horasPorDia[dayKey]) {
        horasPorDia[dayKey] = {};
      }
      const hourKey = date.toLocaleTimeString('es-ES', { hour: '2-digit' }) + ':00';
      horasPorDia[dayKey][hourKey] = (horasPorDia[dayKey][hourKey] || 0) + 1;

      // Todos los eventos por hora con su d√≠a
      const fullHourKey = `${dayKey} ${hourKey}`;
      const existingHour = allHours.find(h => h.key === fullHourKey);
      if (existingHour) {
        existingHour.eventos += 1;
      } else {
        allHours.push({ 
          key: fullHourKey, 
          date: dayKey,
          time: hourKey,
          eventos: 1 
        });
      }
    };

    if (tipoSeleccionado === 'carros') {
      eventosCarros.forEach(e => processEvent(e, false));
    } else if (tipoSeleccionado === 'animales') {
      eventosAnimales.forEach(e => processEvent(e, true));
    } else if (tipoSeleccionado === 'alertas') {
      alertas.forEach(e => processEvent(e, 'alerta'));
    }

    // Ordenar todos los eventos por hora cronol√≥gicamente
    allHours.sort((a, b) => {
      const dateCompare = new Date(a.date) - new Date(b.date);
      if (dateCompare !== 0) return dateCompare;
      return a.time.localeCompare(b.time);
    });

    // Ordenar horas dentro de cada d√≠a
    Object.keys(horasPorDia).forEach(day => {
      const sortedHours = Object.entries(horasPorDia[day])
        .sort(([timeA], [timeB]) => timeA.localeCompare(timeB))
        .map(([time, count]) => ({ time, eventos: count }));
      horasPorDia[day] = sortedHours;
    });

    return {
      daily: allHours, // Usar allHours como datos principales
      horasPorDia,
      allHours
    };
  }, [eventosCarros, eventosAnimales, alertas, tipoSeleccionado]);

  // Obtener datos de horas para el d√≠a seleccionado
  const horasData = diaSeleccionado ? horasPorDia[diaSeleccionado] || [] : [];

  const getBarColor = () => {
    switch (tipoSeleccionado) {
      case 'carros': return '#ff6b6b';
      case 'animales': return '#4ecdc4';
      case 'alertas': return '#ffd93d';
      default: return '#8884d8';
    }
  };

  const getLabel = () => {
    switch (tipoSeleccionado) {
      case 'carros': return 'üöó Carros';
      case 'animales': return 'ü¶Å Animales';
      case 'alertas': return '‚ö†Ô∏è Alertas';
      default: return 'Eventos';
    }
  };

  // Convertir fecha string 'dd/MM/yyyy' a formato con d√≠a de la semana
  const formatDateWithDay = (dateString) => {
    const [day, month, year] = dateString.split('/');
    const dateObj = new Date(year, month - 1, day);
    const dayNames = ['domingo', 'lunes', 'martes', 'mi√©rcoles', 'jueves', 'viernes', 's√°bado'];
    const dayName = dayNames[dateObj.getDay()];
    return `${dayName.charAt(0).toUpperCase() + dayName.slice(1)}, ${dateString}`;
  };

  // Formatear para la gr√°fica principal: d√≠a, fecha y hora
  const formatDateTimeLabel = (item) => {
    return `${formatDateWithDay(item.date)} - ${item.time}`;
  };

  return (
    <div className="history-chart-container">
      {/* Selectores de tipo */}
      <div className="type-selector">
        <button 
          className={`selector-btn ${tipoSeleccionado === 'carros' ? 'active' : ''}`}
          onClick={() => setTipoSeleccionado('carros')}
        >
          üöó Carros
        </button>
        <button 
          className={`selector-btn ${tipoSeleccionado === 'animales' ? 'active' : ''}`}
          onClick={() => setTipoSeleccionado('animales')}
        >
          ü¶Å Animales
        </button>
        <button 
          className={`selector-btn ${tipoSeleccionado === 'alertas' ? 'active' : ''}`}
          onClick={() => setTipoSeleccionado('alertas')}
        >
          ‚ö†Ô∏è Alertas
        </button>
      </div>

      {/* Gr√°fica principal por d√≠as y horas */}
      <div className="history-chart">
        <h3>üìÖ Historial Completo - {getLabel()}</h3>
        <ResponsiveContainer width="100%" height={320}>
          <BarChart data={daily} margin={{ top: 5, right: 30, left: 0, bottom: 100 }}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis 
              dataKey="key" 
              angle={-45}
              textAnchor="end"
              height={120}
              interval={Math.max(0, Math.floor(daily.length / 10))}
            />
            <YAxis />
            <Tooltip 
              contentStyle={{
                backgroundColor: '#1e1e1e',
                border: '1px solid #444',
                borderRadius: '4px',
                color: '#fff'
              }}
              formatter={(value) => [`${value} eventos`, getLabel()]}
              labelFormatter={(label) => {
                const item = daily.find(d => d.key === label);
                return item ? formatDateTimeLabel(item) : label;
              }}
            />
            <Bar 
              dataKey="eventos" 
              fill={getBarColor()} 
              name={getLabel()} 
              isAnimationActive={false}
              onClick={(data) => setDiaSeleccionado(data.date)}
            />
          </BarChart>
        </ResponsiveContainer>
        <p style={{ textAlign: 'center', marginTop: '10px', fontSize: '12px', color: '#888' }}>
          üí° Haz clic en una barra para ver el desgloce por horas de ese d√≠a
        </p>
      </div>

      {/* Desgloce por horas del d√≠a seleccionado */}
      {diaSeleccionado && horasData.length > 0 && (
        <div className="history-chart">
          <h3>‚è∞ Desgloce por Hora - {formatDateWithDay(diaSeleccionado)}</h3>
          <ResponsiveContainer width="100%" height={250}>
            <BarChart data={horasData} margin={{ top: 5, right: 30, left: 0, bottom: 5 }}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="time" />
              <YAxis />
              <Tooltip 
                contentStyle={{
                  backgroundColor: '#1e1e1e',
                  border: '1px solid #444',
                  borderRadius: '4px',
                  color: '#fff'
                }}
                formatter={(value) => [`${value} eventos`, getLabel()]}
                labelFormatter={(label) => `${formatDateWithDay(diaSeleccionado)} a las ${label}`}
              />
              <Bar dataKey="eventos" fill={getBarColor()} name={getLabel()} isAnimationActive={false} />
            </BarChart>
          </ResponsiveContainer>
          <p style={{ textAlign: 'center', marginTop: '10px', fontSize: '12px', color: '#888' }}>
            üìç Mostrando desgloce de horas para: <strong>{formatDateWithDay(diaSeleccionado)}</strong>
            <button 
              onClick={() => setDiaSeleccionado(null)}
              style={{
                marginLeft: '10px',
                padding: '4px 8px',
                backgroundColor: '#ff6b6b',
                color: '#fff',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
                fontSize: '11px'
              }}
            >
              ‚úï Volver
            </button>
          </p>
        </div>
      )}
    </div>
  );
}

export default React.memo(HistoryChart);
