import React, { useState, useMemo } from 'react';
import { ScatterChart, Scatter, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { ZoomIn, ZoomOut } from 'lucide-react';
import '../styles/TimelineChart.css';

function TimelineChart({ eventosCarros, eventosAnimales, alertas }) {
  const [zoomLevel, setZoomLevel] = useState(1);

  // Procesar datos para la l√≠nea de tiempo (cachear)
  const timelineData = useMemo(() => {
    const data = {};

    // Agrupar por fecha completa + hora
    eventosCarros.forEach(evento => {
      const date = new Date(evento.hora_inicio);
      const dateStr = date.toLocaleDateString('es-ES');
      const timeStr = date.toLocaleTimeString('es-ES', { hour: '2-digit', minute: '2-digit' });
      const key = `${dateStr} ${timeStr}`;
      const timestamp = date.getTime(); // Para ordenar despu√©s

      if (!data[key]) {
        data[key] = { dateStr, timeStr, timestamp, carros: 0, animales: 0, alertas: 0 };
      }
      data[key].carros += 1;
    });

    eventosAnimales.forEach(evento => {
      const date = new Date(evento.hora);
      const dateStr = date.toLocaleDateString('es-ES');
      const timeStr = date.toLocaleTimeString('es-ES', { hour: '2-digit', minute: '2-digit' });
      const key = `${dateStr} ${timeStr}`;
      const timestamp = date.getTime();

      if (!data[key]) {
        data[key] = { dateStr, timeStr, timestamp, carros: 0, animales: 0, alertas: 0 };
      }
      data[key].animales += 1;
    });

    alertas.forEach(alerta => {
      const date = new Date(alerta.hora);
      const dateStr = date.toLocaleDateString('es-ES');
      const timeStr = date.toLocaleTimeString('es-ES', { hour: '2-digit', minute: '2-digit' });
      const key = `${dateStr} ${timeStr}`;
      const timestamp = date.getTime();

      if (!data[key]) {
        data[key] = { dateStr, timeStr, timestamp, carros: 0, animales: 0, alertas: 0 };
      }
      data[key].alertas += 1;
    });

    // Convertir a array y ordenar por timestamp (fecha + hora)
    return Object.entries(data)
      .map(([key, value]) => ({
        label: key, // Para el tooltip
        dateStr: value.dateStr,
        timeStr: value.timeStr,
        timestamp: value.timestamp,
        carros: value.carros,
        animales: value.animales,
        alertas: value.alertas,
      }))
      .sort((a, b) => a.timestamp - b.timestamp); // Ordenar por timestamp ascendente
  }, [eventosCarros, eventosAnimales, alertas]);

  const visibleData = useMemo(() => 
    timelineData.slice(-Math.ceil(20 * zoomLevel)),
    [timelineData, zoomLevel]
  );

  const handleZoomIn = () => setZoomLevel(prev => Math.min(prev + 0.5, 3));
  const handleZoomOut = () => setZoomLevel(prev => Math.max(prev - 0.5, 1));

  return (
    <div className="timeline-chart">
      <div className="chart-header">
        <h2>üìä L√≠nea de Tiempo - Eventos por Hora</h2>
        <div className="zoom-controls">
          <button onClick={handleZoomOut} title="Alejar">
            <ZoomOut size={20} /> Alejar
          </button>
          <span className="zoom-level">{Math.round(zoomLevel * 100)}%</span>
          <button onClick={handleZoomIn} title="Acercar">
            <ZoomIn size={20} /> Acercar
          </button>
        </div>
      </div>

      <ResponsiveContainer width="100%" height={300}>
        <ScatterChart data={visibleData} margin={{ top: 5, right: 30, left: 0, bottom: 80 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis 
            dataKey="label" 
            angle={-45}
            textAnchor="end"
            height={100}
            type="category"
          />
          <YAxis />
          <Tooltip 
            contentStyle={{
              backgroundColor: '#1e1e1e',
              border: '1px solid #444',
              borderRadius: '4px',
              color: '#fff'
            }}
            labelFormatter={(label) => label}
          />
          <Legend />
          <Scatter name="Carros" dataKey="carros" fill="#ff6b6b" />
          <Scatter name="Animales" dataKey="animales" fill="#4ecdc4" />
          <Scatter name="Alertas" dataKey="alertas" fill="#ffd93d" />
        </ScatterChart>
      </ResponsiveContainer>

      <div className="chart-stats">
        <div className="stat">
          <span className="stat-label">üöó Total Carros:</span>
          <span className="stat-value">{eventosCarros.length}</span>
        </div>
        <div className="stat">
          <span className="stat-label">ü¶Å Total Animales:</span>
          <span className="stat-value">{eventosAnimales.length}</span>
        </div>
        <div className="stat">
          <span className="stat-label">‚ö†Ô∏è Total Alertas:</span>
          <span className="stat-value">{alertas.length}</span>
        </div>
      </div>
    </div>
  );
}

export default React.memo(TimelineChart);
